def get_segments(src_dir, dst_dir):
    if not os.path.exists(dst_dir):
        os.makedirs(dst_dir)
    for file_name in os.listdir(src_dir):
        src_path=src_dir+file_name
        dst_path=dst_dir+file_name
        if ".DS_" in file_name:
            continue
        
        while True:
            try:
                with open(src_path,'rb') as f:
                    image_base64=base64.b64encode(f.read())
                    status=get_segmentation_base64(image_base64, dst_path)
                if status == 403:
                    continue
                else:
                    if (status != 200):
                        print('unexpected status={}'.format(status))
                    break
            except Exception as e:
                print(e)
                time.sleep(10)

def get_resized_imgs(src_dir, dst_dir):
    for file_name in os.listdir(src_dir):
        if ".DS_" in file_name:
            continue
        src_path=src_dir+file_name
        dst_path=dst_dir+file_name
        src_img=cv.imread(src_path)
        h, w, _ = src_img.shape
        h1 = w*413//295
        dst_img = cv.resize(src_img[h-h1:,:],(295,413))
#         h1 = w*626//413
#         dst_img = cv.resize(src_img[h-h1:,:],(413,626))
        cv.imwrite(dst_path,dst_img)

def merge(src_dir, mask_dir, dst_dir):
    for file_name in os.listdir(src_dir):
        if ".DS_" in file_name:
            continue
        src_img=cv.imread(src_dir+file_name)
        mask_img=cv.imread(mask_dir+file_name)
        
        if mask_img.shape != src_img.shape:
            shape=mask_img.shape[:2]
            src_img=cv.resize(src_img,(shape[1],shape[0]))
#             src_img=cv.resize(src_img,mask_img.shape[:2].transpose((1,0)))
        final_img=mask_img/src_img
        final_img=final_img*src_img+(1-final_img)*255
        final_img=final_img.astype(np.uint8)
        cv.imwrite(dst_dir+file_name, final_img)
        

